function updateMarketValue(e,t){var o=t.querySelector(".meter"),a=t.querySelector(".img");o.style.width=e+"%",a.style.left="calc("+e+"% - 20px)"}var avocadoContainer;document.addEventListener("DOMContentLoaded",function(){avocadoContainer=document.querySelector(".avocado"),bitCoinContainer=document.querySelector(".bitCoin"),houseContainer=document.querySelector(".house"),updateMarketValue(50,avocadoContainer),updateMarketValue(60,bitCoinContainer),updateMarketValue(80,houseContainer)});